				public class ORMapping implements Serializable {

    public String id;
    public String nm;
    public String addr;

    public ORMapping() {
    }

    public ORMapping(String i, String n, String a) {
        id = i;
        nm = n;
        addr = a;
    }

    // getter, setter ...
}

